mapper.select:
SELECT COUNT(*) AS COUNT FROM (
SELECT FCustomerName,FCustomerIdCardNum,FCustomerMobile
FROM t_CreditReport
WHERE FEmpId=#{empId,jdbcType=INTEGER}

  AND  FReportType = #{reportType,jdbcType=INTEGER}

AND FIsDelete = 0
AND DateDiff(dd,FApplyTime,getdate())=0
GROUP BY FCustomerName,FCustomerIdCardNum,FCustomerMobile
) REPORTCOUNT

SELECT COUNT(*) AS COUNT
FROM t_CreditReport
WHERE dateadd (minute,#{time,jdbcType=INTEGER},FApplyTime) > getdate()
AND FEmpId = #{empId,jdbcType=INTEGER}

  AND  FReportType = #{reportType,jdbcType=INTEGER}

SELECT cr.FID AS id, cr.FCustomerName AS customerName, cr.FCustomerIdCardNum AS customerIdCardNum, cr.FCustomerMobile AS customerMobile, cr.FStatus AS status, cr.FUuid AS uuid, emp.FName AS empName, emp.FMobile AS empMobile FROM t_CreditReport cr INNER JOIN t_emp emp ON cr.FEmpId=emp.FEmpID INNER JOIN t_Dept dept ON emp.FDeptId = dept.FID WHERE cr.FStatus = 1 AND cr.FIsDelete = 0 AND cr.FReportType = #{reportType,jdbcType=INTEGER} AND DATEDIFF(MONTH,cr.FApplyTime,GETDATE()) < #{showMonth,jdbcType=INTEGER}
AND emp.FDeptId = #{deptId,jdbcType=INTEGER} AND cr.FEmpId <> #{empId,jdbcType=INTEGER}
AND (dept.FId = #{deptId,jdbcType=INTEGER} OR dept.FBranchId = #{deptId,jdbcType=INTEGER}) AND cr.FEmpId <> #{empId,jdbcType=INTEGER}
AND 1 != 1
ORDER BY cr.FApplyTime DESC

SELECT cr.FID AS id, cr.FCustomerName AS customerName, cr.FCustomerIdCardNum AS customerIdCardNum, cr.FCustomerMobile AS customerMobile, cr.FStatus AS status, cr.FUuid AS uuid, emp.FName AS empName, emp.FMobile AS empMobile FROM t_CreditReport cr INNER JOIN t_emp emp ON cr.FEmpId=emp.FEmpID INNER JOIN t_Dept dept ON emp.FDeptId = dept.FID WHERE cr.FStatus = 1 AND cr.FIsDelete = 0 AND cr.FReportType = #{reportType,jdbcType=INTEGER} AND DATEDIFF(MONTH,cr.FApplyTime,GETDATE()) < #{showMonth,jdbcType=INTEGER}
AND emp.FDeptId = #{deptId,jdbcType=INTEGER}
AND (dept.FId = #{deptId,jdbcType=INTEGER} OR dept.FBranchId = #{deptId,jdbcType=INTEGER})
AND (cr.FCustomerName LIKE '%'+#{queryStr,jdbcType=VARCHAR}+'%' OR cr.FCustomerIdCardNum = #{queryStr,jdbcType=VARCHAR})
ORDER BY cr.FApplyTime DESC

SELECT ts.FQuerier AS customerName, ts.FQuerierPID AS customerIdCardNum, ts.FPhone AS customerMobile FROM t_SelCredit ts INNER JOIN t_emp emp ON ts.fbusinessOwnerID=emp.FEmpID INNER JOIN t_Dept dept ON emp.FDeptId = dept.FID WHERE 1=1
AND ts.FBusinessOwnerID = #{empId,jdbcType=INTEGER}
AND (dept.FID = #{deptId,jdbcType=INTEGER} or dept.fBranchID = #{deptId,jdbcType=INTEGER})
AND dept.FId = #{deptId,jdbcType=INTEGER}
AND ( FQuerier LIKE '%'+#{querierInfo,jdbcType=VARCHAR}+'%' OR FQuerierPID LIKE '%'+#{querierInfo,jdbcType=VARCHAR}+'%' )
AND (ts.FQuerier IS NOT NULL AND ts.FQuerierPID IS NOT NULL) AND (ts.FQuerier != '' AND ts.FQuerierPID != '') AND DATEDIFF(MONTH,ts.FApplyTime,GETDATE()) < #{showMonth,jdbcType=INTEGER} group by ts.FQuerier,ts.FQuerierPID,ts.FPhone ORDER BY ts.FQuerier ASC

SELECT COUNT(1) AS have
FROM t_Emp emp
WHERE emp.FEmpID = #{empId,jdbcType=INTEGER}
AND emp.FEmpID IN (
SELECT FEmpID
FROM t_Emp e
INNER JOIN t_Dept dept on e.FDeptId = dept.FID
WHERE 1=1

  AND dept.FId = #{deptId,jdbcType=INTEGER}


  AND (dept.FId = #{deptId,jdbcType=INTEGER}
  OR dept.FBranchId = #{deptId,jdbcType=INTEGER})


)
